mapper.select:
SELECT comment_id, post_id, parent_comment_id, comment_content, user_id, created_at, updated_at
FROM comment
WHERE comment_id = #{comment_id}

mapper.update:
UPDATE comment
SET comment_content = #{comment_content}
  , updated_at = now()
WHERE comment_id = #{comment_id} and user_id = #{user_id}
